import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function DeleteUserForm() {
  const { userId } = useParams();
  const [user, setUser] = useState({});
  const [deleteOption, setDeleteOption] = useState('name');

  useEffect(() => {
    // Fetch user data based on userId and populate user state
    fetch(`https://jsonplaceholder.typicode.com/users/${userId}`)
      .then(response => response.json())
      .then(data => {
        setUser(data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, [userId]);

  const handleDeleteOptionChange = event => setDeleteOption(event.target.value);

  const handleDelete = () => {
    // Implement your DELETE request logic based on the selected deleteOption
  };

  return (
    <div>
      <h2>Delete User</h2>
      <p>Selected user: {user.name}</p>
      <div>
        <label>
          Delete by:
          <select value={deleteOption} onChange={handleDeleteOptionChange}>
            <option value="name">Name</option>
            <option value="email">Email</option>
            <option value="phone">Phone</option>
          </select>
        </label>
      </div>
      <button onClick={handleDelete}>Delete User</button>
    </div>
  );
}

export default DeleteUserForm;
