import React, { useState, useEffect } from 'react';
import './App.css';
import CreateUserForm from './components/createuserform'; // Correct capitalization
import EditUserForm from './components/edituserform'; // Correct capitalization

function App() {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => setUsers(data))
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const addUser = newUser => {
    setUsers([...users, newUser]);
  };

  const updateUser = updatedUser => {
    setEditingUser(null);
    setUsers(users.map(user => (user.id === updatedUser.id ? updatedUser : user)));
  };

  const deleteUser = userId => {
    setUsers(users.filter(user => user.id !== userId));
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>User Management Application</h1>
      </header>
      <ul className="user-list">
        {users.map(user => (
          <li className="user-item" key={user.id}>
            <strong>{user.name}</strong> - {user.email} - {user.phone}
            <button className="edit-button" onClick={() => setEditingUser(user)}>Edit</button>
            <button className="delete-button" onClick={() => deleteUser(user.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <CreateUserForm addUser={addUser} />
      {editingUser && <EditUserForm user={editingUser} updateUser={updateUser} />}
    </div>
  );
}

export default App;
