import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './EditUserForm.css'; // Import your CSS file for styling

function EditUserForm() {
  const { userId } = useParams();

  const [user, setUser] = useState({});
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');

  useEffect(() => {
    // Fetch user data based on userId and populate form fields
    fetch(`https://jsonplaceholder.typicode.com/users/${userId}`)
      .then(response => response.json())
      .then(data => {
        setUser(data);
        setName(data.name);
        setEmail(data.email);
        setPhone(data.phone);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, [userId]);

  const handleNameChange = event => setName(event.target.value);
  const handleEmailChange = event => setEmail(event.target.value);
  const handlePhoneChange = event => setPhone(event.target.value);

  const handleSubmit = async event => {
    event.preventDefault();

    const updatedUser = {
      id: userId,
      name,
      email,
      phone,
      // Other fields if needed
    };

    try {
      const response = await fetch(`https://jsonplaceholder.typicode.com/users/${userId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedUser),
      });

      if (response.ok) {
        console.log('User updated successfully');
        // You can redirect the user or perform other actions upon success
        // Example: window.location.href = '/';
      } else {
        console.error('Failed to update user');
      }
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <div className="edit-user-form-container">
      <h2>Edit User</h2>
      <form className="edit-user-form" onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={name} onChange={handleNameChange} />
        </label>
        <label>
          Email:
          <input type="email" value={email} onChange={handleEmailChange} />
        </label>
        <label>
          Phone:
          <input type="tel" value={phone} onChange={handlePhoneChange} />
        </label>
        <button className="update-button" type="submit">Update User</button>
      </form>
      {/* Display user details here */}
    </div>
  );
}

export default EditUserForm;
