import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import EditUserForm from './components/edituserform';
import DeleteUserForm from './components/deleteuserform';
import CreateUserForm from './components/createuserform';

function App() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const updateUser = updatedUser => {
    setUsers(users.map(user => (user.id === updatedUser.id ? updatedUser : user)));
  };

  const deleteUser = userId => {
    setUsers(users.filter(user => user.id !== userId));
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>User Management Application</h1>
        </header>
        <Routes>
          <Route
            path="/"
            element={
              <UserList
                users={users}
                updateUser={updateUser}
                deleteUser={deleteUser}
              />
            }
          />
          <Route path="/edit/:userId" element={<EditUserForm updateUser={updateUser} />} />
          <Route path="/delete/:userId" element={<DeleteUserForm deleteUser={deleteUser} />} />
        </Routes>
      </div>
    </Router>
  );
}

function UserList({ users, updateUser, deleteUser }) {
  const handleDelete = userId => {
    deleteUser(userId);
  };

  return (
    <div className="user-list-container">
      <h2>User List</h2>
      <ul className="user-list">
        {users.map(user => (
          <li className="user-item" key={user.id}>
            <div className="user-details">
              <div className="user-info">
                <strong>{user.name}</strong> - {user.email} - {user.phone}
              </div>
              <div className="button-container">
                <Link className="edit-button" to={`/edit/${user.id}`}>Edit</Link>
                <button className="delete-button" onClick={() => handleDelete(user.id)}>Delete</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
      <footer className="footer">
        <CreateUserForm />
      </footer>
    </div>
  );
}

export default App;
