import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import CreateUserForm from './components/createuserform'; // Correct capitalization
import EditUserForm from './components/edituserform'; // Correct capitalization
import DeleteUserForm from './components/deleteuserform'; // Correct capitalization

function App() {
  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>User Management Application</h1>
        </header>
        <Switch>
          <Route exact path="/" component={UserList} />
          <Route path="/edit/:userId" component={EditUserForm} />
          <Route path="/delete/:userId" component={DeleteUserForm} />
        </Switch>
        <CreateUserForm />
      </div>
    </Router>
  );
}

function UserList() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const deleteUser = userId => {
    setUsers(users.filter(user => user.id !== userId));
  };

  return (
    <div>
      <h2>User List</h2>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            <strong>{user.name}</strong> - {user.email} - {user.phone}
            <Link to={`/edit/${user.id}`}>Edit</Link>
            <Link to={`/delete/${user.id}`}>Delete</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
